def get_field_header(field_name, label):
    return "<th class='category'>" + label + "</th>"

def filter_form_get_input(filter, field_name, show_inputs):
    if field_name in filter and filter[field_name] != '':
        return "<input type='text' name='" + field_name + "' value='" + filter[field_name] + "'>"
    elif field_name in show_inputs:
        return "<input type='text' name='" + field_name + "'>"
    else:
        return ''

